import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Radar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis } from 'recharts';

function Chart() {
    const [numbers, setNumbers] = useState([]); // State to store the list of numbers

    useEffect(() => {
        axios.get('http://localhost:5000/data') // Adjust the URL/port as needed
            .then(response => {
                setNumbers(response.data); // Set the fetched numbers to state
            })
            .catch(error => {
                console.error('There was an error fetching the data:', error);
            });
    }, []);

    // Sample data for Chart
    const data = [
        { name: 'Overall Skills', x: numbers[0] * 20 },
        { name: 'Overall Experience', x: numbers[1] },
        { name: 'Impact Keywords', x: numbers[2] },
        { name: 'Job Responsibilites', x: numbers[3] },
        { name: 'Job Summary', x: numbers[4] },
    ];

    const resData = [ 
        { name: 'Overall Skills', x: 4 * 20},
        { name: 'Overall Experience', x: 77.39382051057605 },
        { name: 'Impact Keywords', x: 70.55662101758571 },
        { name: 'Job Responsibilites', x: 69.02663978818158 },
        { name: 'Job Summary', x: 63.10514303296857 },
    ];

    return (
        <div className="results-page-container">
            <div>
                <p>
                    Data from Flask: {numbers[0]}
                    
                </p> {/* Display the message from Flask */}
            </div>
            <div className="chart-display">
                <RadarChart height={300} width={500} 
                    outerRadius="80%" data={data}>
                    <PolarGrid />
                    <PolarAngleAxis dataKey="name" />
                    <PolarRadiusAxis angle={0} domain={[0, 100]} tick={{ fill: 'black'}} />
                    <Radar name="Skills" dataKey="x" stroke="green"
                    fill="green" fillOpacity={0.6} />
                </RadarChart>
                <div className='testing'>
                    <p> Data from Flask: {numbers[0]}</p>
                </div>
            </div>
        </div>
    );
}

export default Chart;
